import React from 'react';
import './App.css';

const api = {
  key : "d0c4ea76926c0692d5a5490265698c56",
  base: "https://api.openweathermap.org/data/2.5"
}

function App() {

  const datebuilder = (d) => {

    let months = ["january", "February", "March", "April", "May", "June", "July", "Auguest", "September", "October", "November", "December"]
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"]


    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`

}

  return (
    <div className="App">
     <main>
       <div className="search-box">
       <input type="text" className="search-bar" placeholder="search..."/>
       </div>

       <div className="location-box">
         <div className="location">Mumbai City, India</div>
         <div className="date">{datebuilder(new Date())}</div>
       </div>

       <div className="weather-box">
          <div className="temp">15 C</div>
          <div className="weather">Sunny1</div>
       </div>

     </main>
    </div>
  );
}

export default App;
